import { useEffect, useState, useId } from "react";
import "./App.css";

const DOGS_API_URLS = {
  allBreeds: "https://dog.ceo/api/breeds/list/all",
  randomImage: "https://dog.ceo/api/breeds/image/random",
  images: "https://dog.ceo/api/breed/hound/images",
};

export const App = () => {
  const [image, setImage] = useState([]);
  const [input, setInput] = useState("");
  const [breed, subBreed] = input.split(" ");
  const MAX_ELEMENTS = "10";
  const id = useId();
  var link = "";
  //First Call
  useEffect(() => {
    console.log("called fetch");
    fetch("https://dog.ceo/api/breeds/image/random/10")
      .then((response) => response.json())
      .then((data) => {
        console.log("response", data);
        setImage(data.message);
      })
      .catch((err) => console.error("received error", err));
  }, []);
  function fullLink() {
    if (subBreed === "") {
      link =
        "https://dog.ceo/api/breed/" + breed + "/images/random/" + MAX_ELEMENTS;
    } else {
      link =
        "https://dog.ceo/api/breed/" +
        breed +
        "/" +
        subBreed +
        "/images/random/" +
        MAX_ELEMENTS;
    }
  }
  // function handleClick() {
  //   fetch(link)
  //     .then((response) => response.json())
  //     .then((data) => {
  //       console.log("response", data);
  //       console.log(breed);
  //       console.log(link);
  //       setImage(data.message);
  //     })
  //     .catch((err) => console.error("received error", err));
  // }
  return (
    <div>
      <div>
        <h1>Razas de perro</h1>
        <input
          id={id}
          value={input}
          onInput={(e) => setInput(e.target.value)}
        ></input>
        <button
          onClick={() => {
            // handleClick();
            fullLink();
            console.log(link);
          }}
        >
          search
        </button>
      </div>
      <div>
        {image.map((img) => (
          <img src={img} alt="" />
        ))}
      </div>
    </div>
  );
};
