import { useEffect, useState, useId } from "react";
import "./App.css";

const DOGS_API_URLS = {
  allBreeds: "https://dog.ceo/api/breeds/list/all",
  randomImage: "https://dog.ceo/api/breeds/image/random",
  images: "https://dog.ceo/api/breed/hound/images",
};

export const App = () => {
  const [image, setImage] = useState([]);
  const [input, setInput] = useState("");
  const [breed, subBreed] = input.split(" ");
  const MAX_ELEMENTS = "10";
  const id = useId();

  //First Call
  useEffect(() => {
    console.log("called fetch");
    fetch("https://dog.ceo/api/breeds/image/random/10")
      .then((response) => response.json())
      .then((data) => {
        console.log("response", data);
        setImage(data.message);
      })
      .catch((err) => console.error("received error", err));
  }, []);
  function fullLink() {
    let link = "https://dog.ceo/api/breed/";
    if (subBreed !== undefined) {
      link += subBreed + "/";
    }
    link += breed + "/images/random/" + MAX_ELEMENTS;
    return link;
  }
  function handleClick() {
    fetch(fullLink())
      .then((response) => response.json())
      .then((data) => {
        console.log("response", data);
        if (data.status !== "error") {
          setImage(data.message);
        }
      })
      .catch((err) => console.error("received error", err));
  }

  return (
    <div>
      <div>
        <h1>Razas de perro</h1>
        <input
          id={id}
          value={input}
          onInput={(e) => setInput(e.target.value)}
        ></input>
        <button
          onClick={() => {
            handleClick();
          }}
        >
          search
        </button>
      </div>
      <div>
        {image.map((img) => (
          <img src={img} alt="" />
        ))}
      </div>
    </div>
  );
};
