import { useState } from "react";

const SearchBar = (props) => {
  const [inputName, setInputName] = useState("");
  let [breed, subBreed] = inputName.split(" ");
  //   const inputResults = { valid: "", breedValue: "", subBreedValue: "" };
  let [valid, setValid] = useState("");
  const MAX_ELEMENTS = "10";

  function breedCheck() {
    console.log("breedCheck");
    let temp = "";
    if (subBreed !== undefined) {
      temp = breed;
      breed = subBreed;
      subBreed = temp;
    }
    if (this.props.breedList[breed] !== undefined) {
      if (subBreed !== undefined) {
        if (this.porps.breedList[breed].includes(subBreed) === true) {
          valid = "true";
        } else {
          valid = "false";
        }
      } else {
        console.log("Is Breed");
        valid = "true";
      }
    } else {
      console.log("Not Breed");
      valid = "false";
    }
  }

  function fullLink() {
    console.log("fullLink");
    breedCheck();
    let link = "https://dog.ceo/api/breed/" + breed;
    if (subBreed !== undefined) {
      link += "/" + subBreed;
    }
    link += "/images/random/" + MAX_ELEMENTS;
    console.log(link);
    return link;
  }

  const onButtonPress = () => {
    console.log(this.props.breedList);
    console.log("onButtonPress");
    props.onClick(fullLink());
  };

  return (
    <div>
      <input value={inputName} onChange={(e) => setInputName(e.target.value)} />
      <button onClick={onButtonPress}>Search</button>
    </div>
  );
};
export default SearchBar;
